import { createAsyncThunk } from "@reduxjs/toolkit";
import instance from "../../Http/Settings";

export const getRondomInfo = createAsyncThunk(" rondomInfo/ getRondomInfo", async (elem, { rejectWithValue, dispatch }) => {
    const response = await instance.get(`filter.php?i=${elem}`);
    dispatch(rondomInfoMeal(response.data.drinks));
});
